import React, {useState, useEffect} from'react';
import {Container, AppBar, Typography, Grow, Grid} from '@material-ui/core';
import { useDispatch } from 'react-redux'; //dispatch an action

import{ getPosts } from './actions/posts';
import journal from './images/journal.jpg';
import Posts from './components/Posts/Posts';
import Form from './components/Form/Form';
import useStyles from './styles';

const App = () => {
    const [currentId, setCurrentId] = useState(null);
    const classes = useStyles();
    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(getPosts()); //dispatch an action
    },[currentId,dispatch]);
    return(
        <Container maxWidth="lg">
            <AppBar className={classes.appBar} position="static" color="inherit" >
            <Typography className={classes.heading} variant="h2" align='center'>Your Dream Journal</Typography>
            <img className={classes.image} src={journal} alt="memories" height="60"/>
            </AppBar>   
            <Grow in>
                <Container>
                    <Grid container="space-between" alignItems="stretch" spacing={3}>
                        <Grid item xs={12} sm={7}>  
                            <Posts urrentId={currentId} setCurrentId={setCurrentId}/>
                        </Grid>
                        <Grid item xs={12} sm={4}>
                            <Form currentId={currentId} setCurrentId={setCurrentId}/>
                        </Grid>
                    </Grid>
                </Container>
            </Grow>        
        </Container>
    )
}

export default App
