import React, {useState, useEffect} from 'react';
import { TextField,Button,Typography, Paper} from '@material-ui/core';
import { useDispatch, useSelector } from 'react-redux';
import FileBase from 'react-file-base64';

import useStyles from './styles';
import { createPost, updatePost } from '../../actions/posts';
//TODO: ADD DATE

//GET CURRENT ID
const Form = ({currentId, setCurrentId}) => {
    const [postData, setPostData] = useState({creator: '', title: '', message: '', tags: '', selectedFile: ''});
    const post = useSelector((state) => currentId ? state.posts.find(p => p.id === currentId) : null);
    const classes = useStyles();
    const dispatch = useDispatch();

    useEffect(() => {   //populate the form when the editing an entry
        if (post) {
            setPostData(post);
        }
    }, [post]);

    const handleSubmit= (e) => {//send post request with user input
        e.preventDefault();

        if(currentId){
            dispatch(updatePost(currentId, postData));
        } else {
            dispatch(createPost(postData));
        }
    }
     const clear = () => {

    }
    return (
       <Paper className={classes.paper}>
            <form autoComplete='off' noValidate className={`${classes.root} ${classes.form}`} onSubmit={handleSubmit}>
            <Typography variant='h6'> ${currentId ? 'Editing': 'Creating'} a Jornal Entry</Typography>
            <TextField name="creator" variant='outlined' label='Creator' fullWidth value={postData.creator} onChange={(e) => setPostData({...postData, creator: e.target.value})}/>
            <TextField name="title" variant='outlined' label='Title' fullWidth value={postData.title} onChange={(e) => setPostData({...postData, title: e.target.value})}/>
            <TextField name="message" variant='outlined' label='Message' fullWidth value={postData.message} onChange={(e) => setPostData({...postData, message: e.target.value})}/>
            <TextField name="tags" variant='outlined' label='Tags' fullWidth value={postData.tags} onChange={(e) => setPostData({...postData, tags: e.target.value})}/>
            <div className={classes.fileInput}>
                <FileBase name="file" type="file" multiple={false} onDone={({base64}) => setPostData({...postData,selectedFile: base64})}/>
                <Button className={classes.buttonSubmit} variant="contained" color="primary" size="large" type="submit" fullWidth>Submit</Button>
                <Button  variant="contained" color="secondary" size="small" onClick={clear} fullWidth>Clear</Button>
            </div>
            </form>
       </Paper>
    );
}

export default Form;
