import {AUTH,LOGOUT} from '../constants/actionTypes';

const authReducer = (state, action) => {
    switch (action.type) {
        case AUTH:
            return {...state, isAuthenticated: action.isAuthenticated};
        case LOGOUT:
            return {...state, isAuthenticated: false};
        default:
            return state;
    }
}
export default authReducer;
